import React, { useEffect, useState } from 'react';
import Items from '../components/Common/Item';
import { fetchItems } from '../redux/items/operations';
import { getItems } from '../redux/items/selectors';
import { useDispatch, useSelector } from 'react-redux';
import MainImage from '../assets/img/r-img/main-background.png';
import { fetchCarts } from '../redux/carts/operations';
import Header from '../components/Common/Header';

const Home = () => {
    const selector = useSelector(state => state);
    const dispatch = useDispatch();
    const items = getItems(selector);

    useEffect(() => {
        dispatch(fetchItems());
        if (localStorage.getItem('LOGIN_USER_KEY')) {
            dispatch(fetchCarts());
            console.log(items);
        }
    }, []);

    return (
        <>
        <Header />
            <section className='main-image'>
                <img src={MainImage} alt="main-image" />
            </section>
            <div className='product-heading'>
                <h2>Selected just for you !</h2>
            </div>
            <section className='item-container'>
                <div className='item-grid'>
                        {items &&
                        items.map(item => (
                                <div className='item'>
                                    <li>
                                    <Items key={item.id} item={item} />
                                    </li>
                                </div>
                            ))}
                </div>
            </section>
            <div className='product-heading'>
                <h2>Recommended Drinks</h2>
            </div>
            <section className='item-container'>
                <div className='item-grid'>
                        {items &&
                        items.map(item => (
                                <div className='item'>
                                    <li>
                                    <Items key={item.id} item={item} />
                                    </li>
                                </div>
                            ))}
                </div>
            </section>
            <div className='product-heading'>
                <h2>Favourite Drinks</h2>
            </div>
            <section className='item-container'>
                <div className='item-grid'>
                        {items &&
                        items.map(item => (
                                <div className='item'>
                                    <li>
                                    <Items key={item.id} item={item} />
                                    </li>
                                </div>
                            ))}
                </div>
            </section>
        </>
    );
};

export default Home;
